import React from "react";
import HeaderComponent from "./HeaderComponent";
import FooterComponent from "./FooterComponent";

const EditarTComponent = () => {

    return (
        <div>
            <img class="torta" src="img/torta_cafe_late_pasteleria_la_palmera.jpg" />
            <section class="section" style="font: size 1vw">
                <h1 class="Tituloeditar">Elige los detalles de tu torta:</h1>
                <div class="input-group mb-3">
                    <label class="input-group-text" for="inputGroupSelect01">Tipo Pasteleria</label>
                    <select class="form-select" id="inputGroupSelect01">
                        <option selected>Elegir...</option>
                        <option value="1">Vegana</option>
                        <option value="2">No Vegana</option>
                    </select>
                    </div>
            </section>
        </div>
    );




}





export default EditarTComponent;
